					smpteLineF2 = smpteLineF1;
				if (!NTV2_VIDEO_FORMAT_HAS_PROGRESSIVE_PICTURE(vFormat)  &&  !isF2)
					CHECK(fd.GetSMPTELineNumber(1, smpteLineF2, isF2));

